import React from "react";
import ReactDOM from "react-dom";
import { useController, useForm } from "react-hook-form";
import { z } from "zod";
import { zodResolver } from '@hookform/resolvers/zod';

import "./styles.css";

function App() {
  const schema = z.object({
    firstName: z.string().min(1, { message: 'firstName Required' }),
    lastName: z.string().min(1, { message: 'lastName Required' }),
  });
  const {
    register,
    control,
    formState: { errors },
    handleSubmit
  } = useForm({
    mode: "all", // "onChange"
    resolver: zodResolver(schema),
  });
  const onSubmit = (data) => {
    alert(JSON.stringify(data));
  };
  const {field,fieldState: { isTouched }} = useController({name:'firstName' ,control})

  console.log("field",field)
  console.log("isTouched",isTouched)

  return (
    <div className="App">
      <form onSubmit={handleSubmit(onSubmit)}>
        <div>
          <label htmlFor="firstName">First Name</label>
          <input
            placeholder="bill"
            {...register("firstName", { required: true, maxLength: 2 })}
            onChange={register.onChange}
          />
          {errors.firstName && isTouched && <p>focus {errors.firstName.message}</p>}
          {errors.firstName &&!isTouched && <p>not focus{errors.firstName.message}</p>}
        </div>

        <div>
          <label htmlFor="lastName">Last Name</label>
          <input
            placeholder="luo"
            {...register("lastName", { required: true })}
          />
          {errors.lastName && <p>{errors.lastName.message}</p>}
        </div>

        <div>
          <label htmlFor="email" placeholder="bluebill1049@hotmail.com">
            Email
          </label>
          <input {...register("email", { required: true })} />
          {errors.email && <p>This is required</p>}
        </div>
        <input type="submit" />
      </form>
    </div>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
