import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import {
  FormProvider,
  useController,
  useForm,
  useFormContext,
} from 'react-hook-form';
import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';

import './styles.css';

function App() {
  const schema = z.object({
    firstName: z
      .string()
      .min(1, { message: 'firstName Required' })
      .max(2, 'firstName max 2'),
    lastName: z
      .string()
      .min(1, { message: 'lastName Required' })
      .max(5, 'lastName max 5'),
  });
  const methods = useForm({
    mode: 'all', // 	Validation is triggered on both blur and change events.
    resolver: zodResolver(schema),
  });
  const onSubmit = (data: unknown) => {
    alert(JSON.stringify(data));
  };

  return (
    <div className='App'>
      <FormProvider {...methods}>
        <form onSubmit={methods.handleSubmit(onSubmit)}>
          <Input name={'firstName'} placeholder='firstName' />
          <Input name={'lastName'} placeholder='lastName' />
          <input type='submit' />
        </form>
      </FormProvider>
    </div>
  );
}

const Input = ({
  name,
  placeholder,
}: {
  name: string;
  placeholder: string;
}) => {
  // focus中か制御
  const [isFocus, setIsFocus] = useState(false);
  const { register, control } = useFormContext();
  const {
    formState: { errors },
    fieldState: { isTouched }, // 一回でもinputにフォーカスしたか否か
  } = useController({
    name,
    control,
  });

  return (
    <div>
      <label htmlFor={name}>First Name</label>
      {errors.firstName && isFocus && (
        <p>focus {errors.firstName?.message}</p>
      )}
      <input
        placeholder={placeholder}
        {...register(name, {
          required: true,
          onChange: () => setIsFocus(true),
          onBlur: () => setIsFocus(false),
        })}
        onFocus={() => setIsFocus(true)} // focus中か制御
      />
      {errors.firstName && isTouched && !isFocus && (
        <p>not focus{errors.firstName?.messag}</p>
      )}
    </div>
  );
};

const rootElement = document.getElementById('root');
ReactDOM.render(<App />, rootElement);
